  const history= useNavigate();
  const location = useLocation();
  const {userid}= useParams();
  console.log("userid is "+ userid);
  const [userdata, setdata]= useState({});
  const [advt, setadvt] = useState({
    title:"", description:"", budget:0, link:""});

    let name, value;
    const handleinput= (e) =>{
        name= e.target.name;
        value= e.target.value;
        setadvt({...advt, [name]:value});
    }

  const callinfo = async()=>{
    try{
        console.log('welcome');
        const res= await fetch(`/${userid}/info`,{
            method: "GET",
            headers: {
                Accept: "application/json",
                "Content-Type":"application/json"
            },
            credentials: "include"
        });
        const data= await res.json();
        if(data=="-2")
        {
            window.alert('You must be logged in first');
            history('/userlogin');
        }
        else
        {
          console.log(data);
        setdata(data);
        }
    }
    catch(err){
        console.log(err);
    }
}
const username= userdata.username;

const send =async(e)=>{
  e.preventDefault();
  const {title, description, budget, link}= advt;
  try{
    if(title===""||description===""||budget==" "||link==="")
        {
            window.alert("Data is empty");
        }
        else{
    console.log("advt is "+ advt);
    const res= await fetch('https://ad-check.herokuapp.com/createad',{
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type":"application/json"
        },
        body: JSON.stringify({
          title:title,
          description:description,
          budget:budget,
          link:link,
          check:"OK"
        })
    });
    const data= await res.json();
    console.log('req is');
    console.log(data);
    if(data=="-8")
    {
        window.alert('Invalid data');
    }
    else
    {
        window.alert('Ad added successfully!');
    }
    console.log(data);
    window.location.reload();
    setadvt({title:"",description:"",budget: 0, link:"",});
  }
}
catch(err){
    console.log(err);
}
}

useEffect(() => {
  callinfo();
},[]);

  return (
  <div>
  <div className="container-fluid">
  <div className='row' style={{marginBottom: '30px'}}>
  <div className='col-6'>
      <h1 style={{marginTop: '20px', textAlign: 'center', fontWeight: 'bold'}}>Hello {username}</h1>
      </div>
      
      </div>
      </div>
  </div>)
};

export default User;
